sys_script_include.script:
var bjDataUtils = Class.create();
bjDataUtils.prototype = {
	initialize: function() {},

	getRecord: function(table, sysId, fields) {
		/**
		 * Retrieves data from one record from a table
		 *
		 * @param {string} table - the table to query
		 * @param {string} sysId - sys_id of the record to be retrieved
		 * @param {array} fields - fields to be returned
		 * @returns {Object} - object containing field/value pairs, where field contains value/display value as key
		 */
		var gr = new GlideRecord(table);


		if (gr.get(sysId)){
			var record = {};

			// make sure the return list always contains the sys_id
			if (fields.indexOf('sys_id') === -1) {
				record.sys_id = {
					value: sysId,
					display: sysId
				}
			}

			// calculate the actual table where the data are stored, this allows for
			// querying on parent tables. if a query is set on cmdb_ci, a retrieved cmdb_ci record
			// will not contain all fields if its class is cmdb_ci_computer.
			var refClassName = 'ref_'+gr.getValue('sys_class_name');

			fields.forEach(function(field) {
				record[field] = {
					value: gr[refClassName][field].getValue(),
					display: gr[refClassName][field].getDisplayValue()
				};
			});
		}

		return record;
	},

	getData: function(table, query, fields, max) {
		/**
		 * Retrieves data from a table
		 *
		 * @param {string} table - the table to query
		 * @param {string} query - encoded query string
		 * @param {array} fields - fields to be returned
		 * @param {number} max - the number of records to be returned, -1 returns all
		 * @returns {array} - array with records and name value pairs of the fields (both display and value).
		 */
		gs.info('bjDataUtils#getDataIncludeExtendedTables')
		var gr = new GlideRecord(table);

		if (fields.indexOf('sys_id') === -1) {
			fields.push('sys_id');
		}
		gr.addQuery(query);
		if (typeof max === 'number' && max > -1) {
			gr.setLimit(max);
		}
		gr.query();

		var result = [];
		var record;
		var refClassName;

		while (gr.next()) {
			record = {};
			refClassName = 'ref_'+gr.getValue('sys_class_name');
			fields.forEach(function(field) {
				record[field] = {
					value: gr[refClassName][field].getValue(),
					display: gr[refClassName][field].getDisplayValue()
				};
			});
			result.push(record);
		}
		return result;
	},

	type: 'bjDataUtils'
};
